const emitEvent   = require("./lib/emitEvent.js"  );
const addEvent    = require("./lib/addEvent.js"   );
const deleteEvent = require("./lib/deleteEvent.js");
const getEvent    = require("./lib/getEvent.js");

class EventEmitter {
	constructor() {
		this.Events = {};
	}
	on(name, callback) {
		return addEvent.bind(this.Events)(name, callback, false);
	}
	once(name, callback) {
		return addEvent.bind(this.Events)(name, callback, true );
	}
	off(id) {
		return deleteEvent.bind(this)(id);
	}
	emit() {
		return emitEvent.bind(this.Events)(...arguments);
	}
	get(name) {
		return getEvent.bind(this.Events)(name);
	}
}

module.exports = EventEmitter;