function deleteEvent(id) {
	if (typeof id !== "string") throw new Error("id can only be a string.");
		
	let deleted = false;
		
	for (let i = 0, keys = Object.keys(this.Events); i < keys.length; i++) {
		let e = this.Events[keys[i]];
			
		for (let o = 0; o < e.length; o++) {
			let ev = e[o];
				
			if (ev.id === id) {
				deleted = true;
				
				delete this.Events[keys[i]][o];

				if (Object.keys(this.Events[keys[i]]).length === 0) delete this.Events[keys[i]];
			}
		}
			
		if (typeof this.Events[keys[i]] === "object") this.Events[keys[i]] = this.Events[keys[i]].filter(ev => ev ? true : false);
	}
		
	return deleted;
}

module.exports = deleteEvent;